/* Navbar styling */
.navbar-light {
    background-color: #f8f9fa; /* Light background */
    border-bottom: 1px solid #e5e5e5; /* Bottom border */
}

.navbar-expand-lg .navbar-nav .nav-link {
    color: #000; /* Text color */
    font-weight: bold; /* Make the text bold */
    margin-right: 15px; /* Space between links */
}

/* Container styling */
.container {
    max-width: 1140px;
    margin: 0 auto;
    padding: 15px;
}

.my-4 {
    margin-top: 1.5rem;
    margin-bottom: 1.5rem;
}

/* Footer styling */
.web-footer {
    background-color: #f1f1f1;
    padding: 20px 0;
    text-align: center;
    font-size: 0.875rem;
    color: #666;
}

/* Breadcrumbs styling */
.page-breadcrumbs {
    padding: 10px 15px;
    background-color: #e9ecef;
    border-radius: 4px;
    font-size: 0.9rem;
}

/* Button styling */
button {
    background-color: #007bff;
    color: #fff;
    border: none;
    padding: 10px 20px;
    border-radius: 5px;
    cursor: pointer;
}

button:hover {
    background-color: #0056b3;
}

/* Main content styling */
main.container {
    background-color: #ffffff;
    padding: 20px;
    box-shadow: 0 2px 5px rgba(0, 0, 0, 0.1);
    border-radius: 8px;
}

/* General styles for form inputs (if any) */
input[type="text"], input[type="email"], input[type="password"], textarea {
    width: 100%;
    padding: 10px;
    margin: 10px 0;
    border: 1px solid #ccc;
    border-radius: 4px;
}

input[type="submit"], button[type="submit"] {
    background-color: #28a745;
    color: white;
    padding: 12px 20px;
    border: none;
    border-radius: 4px;
    cursor: pointer;
}

input[type="submit"]:hover, button[type="submit"]:hover {
    background-color: #218838;
}

